import{_ as e,c as a,o as i,a as t}from"./app.550b9678.js";const r="/assets/kit_01.33df1c3a.jpg",o="/assets/kit_02.aa3a70ac.jpg",l="/assets/kit_03.19dc581e.jpg",s="/assets/kit_05.1b9bfa99.jpg",n="/assets/kit_06.99ea429e.jpg",p="/assets/kit_07.fa10cb0e.jpg",c="/assets/kit_08.93ddcd29.jpg",d="/assets/kit_09.34b2f45f.jpg",m="/assets/kit_10.986fd0aa.jpg",_="/assets/kit_11.abadf55e.jpg",b="/assets/kit_12.6a28d3c1.jpg",g="/assets/kit_13.266553a5.jpg",u="/assets/kit_14.9b1e9079.jpg",h="/assets/kit_15.1b1d8d17.jpg",f="/assets/kit_16.bb4dd748.jpg",v="/assets/kit_18.f443e6ed.jpg",k="/assets/kit_17.ded1fe94.jpg",z="/assets/kit_19.e75829b1.jpg",j="/assets/kit_20.35c704a0.jpg",M="/assets/kit_22.fbc4c09b.jpg",S="/assets/kit_23.72b7a483.jpg",B="/assets/kit_24.6f0bdfa2.jpg",P="/assets/kit_26.3db198f6.jpg",T="/assets/kit_25.3584ee36.jpg",y="/assets/connectors_side.b2dd2510.jpg",A="/assets/kit_32.b166bf0b.jpg",R="/assets/kit_34.a49dfcce.jpg",x="/assets/kit_35.8c94e2c0.jpg",I="/assets/kit_27.47509ab3.jpg",C="/assets/kit_27b.70b5b2d7.jpg",E="/assets/kit_28.4feac2b9.jpg",N="/assets/kit_29.cd61ab16.jpg",U="/assets/kit_30.0e0ba81c.jpg",V="/assets/kit_31.a1aa8473.jpg",D="/assets/kit_37.c613eb0d.jpg",F="/assets/kit_38.7e79b88c.jpg",O="/assets/kit_39.f43e21e4.jpg",q="/assets/kit_40.f1ce1d2b.jpg",Z="/assets/kit_41.38aec472.jpg",$="/assets/kit_42.8c4eb1be.jpg",w="/assets/kit_44.8a6c09cf.jpg",L="/assets/kit_43.254d6f16.jpg",J="/assets/kit_45.69fa8c82.jpg",G="/assets/kit_47.1c7c472c.jpg",oe=JSON.parse('{"title":"Montaggio del kit","description":"","frontmatter":{},"headers":[{"level":2,"title":"Elenco delle componenti del kit","slug":"elenco-delle-componenti-del-kit","link":"#elenco-delle-componenti-del-kit","children":[]},{"level":2,"title":"1 - Motori","slug":"_1-motori","link":"#_1-motori","children":[]},{"level":2,"title":"2 - Sfera","slug":"_2-sfera","link":"#_2-sfera","children":[]},{"level":2,"title":"3 - Raspberry Pi","slug":"_3-raspberry-pi","link":"#_3-raspberry-pi","children":[]},{"level":2,"title":"4 - Paratie anteriore e laterali","slug":"_4-paratie-anteriore-e-laterali","link":"#_4-paratie-anteriore-e-laterali","children":[]},{"level":2,"title":"5 - Batteria e collegamenti","slug":"_5-batteria-e-collegamenti","link":"#_5-batteria-e-collegamenti","children":[]},{"level":2,"title":"6 - Telecamera","slug":"_6-telecamera","link":"#_6-telecamera","children":[]},{"level":2,"title":"7 - Coperchio, ruote e altoparlante","slug":"_7-coperchio-ruote-e-altoparlante","link":"#_7-coperchio-ruote-e-altoparlante","children":[]},{"level":2,"title":"8 - Accensione","slug":"_8-accensione","link":"#_8-accensione","children":[]}],"relativePath":"kit/index.md"}'),H={name:"kit/index.md"},K=t('<h1 id="montaggio-del-kit" tabindex="-1">Montaggio del kit <a class="header-anchor" href="#montaggio-del-kit" aria-hidden="true">#</a></h1><p>Seguendo questi passi, assemblerai CoderBot partendo dal suo kit.</p><h2 id="elenco-delle-componenti-del-kit" tabindex="-1">Elenco delle componenti del kit <a class="header-anchor" href="#elenco-delle-componenti-del-kit" aria-hidden="true">#</a></h2><p><img src="'+r+'" alt=""></p><ol><li>Raspberry Pi Model B 3 + Scheda controllo motori e sensori + Microfono USB</li><li>Altoparlante autoalimentato</li><li>Batteria al litio ricaricabile</li><li>Sensori a ultrasuoni + cavi di collegamento</li><li>Telecamera + cavo piatto</li><li>Cavo USB - MicroUSB - angolo 90° (per alimentazione Raspberry)</li><li>Cavo USB - MicroUSB - piatto (per alimentazione motori)</li><li>Cavo USB - MicroUSB - piatto (per ricarica batteria)</li><li>Telaio legno/metallo</li><li>Motori + cavi di collegamento</li><li>Ruote</li><li>Sfera</li><li>6 Viti M3 10mm</li><li>3 Viti M3 30mm</li><li>6 Dadi M3 30mm</li><li>2 Distanziali 20mm</li><li>4 Viti M2.5 12mm + 4 Distanziali cavi 4mm + 4 Dadi M2.5</li><li>Viti M2 6mm +Dadi M2</li></ol><h2 id="_1-motori" tabindex="-1">1 - Motori <a class="header-anchor" href="#_1-motori" aria-hidden="true">#</a></h2><p>1.1 Infilare i supporti in metallo a forma di “T” nel telaio. <img src="'+o+'" alt=""></p><br><p>1.2 Posizionare i motori in modo che il perno bianco sia rivolto all&#39;esterno della base e che il filo di alimentazione rosso sia vicino alla base, il rosso più in alto.</p><p><img src="'+l+'" alt=""><br><br></p><p>1.3 Posizionare le viti M3 da 30mm nei fori. <img src="'+s+'" alt=""><br><br></p><p>1.4 Posizionare i bulloni M3 autobloccanti dalla parte opposta. <img src="'+n+'" alt=""><br><br></p><p>1.5 Serrare viti e bulloni, è opportuno utilizzare delle pinze o una chiave per tenere fermo il dado. <img src="'+p+'" alt=""></p><p>1.6 Far passare i cavi di alimentazione nelle feritoie vicino ai contatti.</p><p>Ripetere per l’altro motore</p><h2 id="_2-sfera" tabindex="-1">2 - Sfera <a class="header-anchor" href="#_2-sfera" aria-hidden="true">#</a></h2><p>2.1 Fissare i distanziali da 20mm alla sfera utilizzando due viti M3 da 10mm. <img src="'+c+'" alt=""><br><br></p><p>2.2 fissare sfera + distanziali alla parte inferiore del telaio, stringere bene le viti e i dadi <img src="'+d+'" alt=""></p><div class="warning custom-block"><p class="custom-block-title">ATTENZIONE</p><p>La sfera deve essere fissata nella parte inferiore del telaio, ovvero dalla stessa parte dove si trovano i motori.</p></div><p><img src="'+m+'" alt=""></p><h2 id="_3-raspberry-pi" tabindex="-1">3 - Raspberry Pi <a class="header-anchor" href="#_3-raspberry-pi" aria-hidden="true">#</a></h2><p>3.1 Posizionare i distanziali in plastica bianca sopra la base, sempre in corrispondenza dei fori. <img src="'+_+'" alt=""><br><br></p><p>3.3 Posizionare il Raspberry Pi sopra i distanziali <img src="'+b+'" alt=""></p><div class="warning custom-block"><p class="custom-block-title">ATTENZIONE</p><p>Il Raspberry deve essere posizionato in modo che le porte USB siano rivolte verso la parte più corta del supporto in legno (il lato opposto alla finestrella). Nella foto a destra: particolare di una vite con distanziale fissato correttamente</p></div><p><br><br> 3.4 Infilare le viti di fissaggio nei fori sul Raspberry Pi <img src="'+g+'" alt=""><br><br></p><p>3.5 Avvitare e serrare i dadi alle viti sporgenti sotto la base in legno. <img src="'+u+'" alt=""><br><br></p><h2 id="_4-paratie-anteriore-e-laterali" tabindex="-1">4 - Paratie anteriore e laterali <a class="header-anchor" href="#_4-paratie-anteriore-e-laterali" aria-hidden="true">#</a></h2><p>4.1 Appoggiare le paratie laterali in modo da far entrare gli incastri nel telaio, senza farle scorrere. La paratia con la finestra laterale deve essere montata sulla parte destra del robot, tenendo conto che i motori sono nella parte anteriore e la sfera nella parte posteriore.</p><p><img src="'+h+'" alt=""></p><p><img src="'+f+'" alt=""><br><br></p><p>4.2 Posizionare i sensori sonar nei fori presenti nelle paratie. Il sensore da posiziore nella paratia anteriore è quello con il connettore perpendicolare alla scheda.</p><div class="warning custom-block"><p class="custom-block-title">ATTENZIONE</p><p>E&#39; possibile che sia necessario esercitare una cera forza per far entrare i sensori nelle paratie, sia quella in legno che quelle in metallo.</p></div><p><img src="'+v+'" alt=""></p><p><img src="'+k+'" alt=""></p><p><img src="'+z+'" alt=""></p><p><img src="'+j+'" alt=""></p><p><br><br></p><p>4.3 Appoggiare la paratia anteriore in modo da farla incastrare nelle feritoie sulle paratie laterali. <br><br></p><p>4.4 Appoggiare il supporto con il Raspberry Pi già montato all&#39;interno del telaio, tra le tre paratie, facendo attenzione che i supporti si incastrino nelle feritoie presenti sulle paratie laterali. <img src="'+M+'" alt=""><br><br></p><p>4.5 Far scorre le paratie verso la parte posteriore del telaio in modo da incastrarsi in esso. <img src="'+S+'" alt=""><br><br></p><p>4.6 Posizionare la vite M3 10mm nel foro posto nella parte anteriore del telaio e fissarla con un dado autobloccante M3. <img src="'+B+'" alt=""></p><h2 id="_5-batteria-e-collegamenti" tabindex="-1">5 - Batteria e collegamenti <a class="header-anchor" href="#_5-batteria-e-collegamenti" aria-hidden="true">#</a></h2><br><p>5.1 Collegare i cavi di alimentazione dei motori: quello del motore destro sullo spinotto più a destra. <img src="'+P+'" alt=""><br><br></p><p>5.2 Collegare i cavi dei sensori a ultrasuoni: il cavo del sensore centrale, che passa sotto il raspberry e atteaverso la feritoria nella parte posteriore del supporto, deve essere inserito nel connettore più vicina alla parte anteriore. Il cavo del sensore sinistro deve essere posizionato nel connettore al centro e infine il cavo del sensore destro nel connettore più vicina alla parte posteriore.</p><p><img src="'+T+'" alt=""></p><div class="danger custom-block"><p class="custom-block-title">ATTENZIONE</p><p>Scambiare il verso del connettore comporta invertire la polarità dell&#39;alimentazione del sensore che, oltre a non funzionare, potrebbe danneggiarsi. Assicurarsi dell&#39;orientamento dei connettori: devono essere inseriti in modo che i fili di colore bianco siano dal lato della scritta CoderBot stampata sulla scheda, come mostrato in figura. <br><br><img src="'+y+'" alt=""></p></div><p><br><br> 5.3 Posizionare la batteria all&#39;interno del telaio, sotto il Raspberry; la batteria deve essere posizionata in modo che il display dello stato di carica sia rivolto verso il basso e i connettori USB verso la parte posteriore.</p><p><img src="'+A+'" alt=""><br><br><br></p><p>5.4 Collegare i cavi USB - Micro USB in dalla batteria al Raspberry e alla scheda motori. Il cavo più lungo, con i connettori orientati a 90°, deve essere fatto passare dall&#39;interno del telaio all&#39;esterno della finestra laterla per poi rientrare subito ed essere inserito nella presa di alimentazione del Raspberry.</p><p><img src="'+R+'" alt=""></p><p><br><br></p><p>5.5 Collegare il cavo più corto, che deve essere usato per collegare la batteria alla scheda motori, restando all&#39;interno del telaio. <img src="'+x+'" alt=""></p><h2 id="_6-telecamera" tabindex="-1">6 - Telecamera <a class="header-anchor" href="#_6-telecamera" aria-hidden="true">#</a></h2><p><img src="'+I+'" alt=""></p><p>6.1 Posizionare la telecamera sul supporto in modo che la finestrella, asimmetrica, lasci scoperto il led sulla parte destra della scheda della telecamera. <img src="'+C+'" alt=""></p><p><br><br></p><p>6.2 Fissare la telecamera con le viti M2 6mm e relativi bulloni. Posizionare le viti in modo che la testa stia dalla parte del legno e i bulloni dalla parte della scheda.</p><div class="danger custom-block"><p class="custom-block-title">ATTENZIONE</p><p>La parte posteriore della telecamera è costituita da un circuito stampato con diversi componenti delicati. Nello stringere i dadi, si raccomanda di fare attenzione a non utilizzare pinze o utensili che possano danneggiare il circuito. A tal proposito si consiglia di usare solo le dita per tener fermi i dadi o, al più, una pinzetta molto piccola.</p></div><p><img src="'+E+'" alt=""></p><p><br><br> 6.3 Far passare il cavo piatto dalla telecamera fino al farlo scorrere nel connettore posto sul Raspberry. Verificare che la linguetta di fissaggio posta sul connettore sia estratta. Una volta che il cavo piatto è ben inserito nel connettore, spingere la linguetta verso il basso, in modo da fissare il cavo.</p><p><img src="'+N+'" alt=""></p><p><br><br></p><p>6.4 Posizionare il supporto della telecamera sul telaio principale in corrispondenza delle scanalature.</p><p><img src="'+U+'" alt=""></p><p><br><br> 6.5 Infilare, avendo cura di fare in modo che le parti si incastrino correttamente l&#39;una nell&#39;altra il supporto telecamera nelle scanalature sulle paratie, fino a che non il supporto non sia ben incastrato.</p><p><img src="'+V+'" alt=""></p><p><br><br></p><h2 id="_7-coperchio-ruote-e-altoparlante" tabindex="-1">7 - Coperchio, ruote e altoparlante <a class="header-anchor" href="#_7-coperchio-ruote-e-altoparlante" aria-hidden="true">#</a></h2><p><img src="'+D+'" alt=""></p><p>7.1 Far passare il cavetto con il jack audio da 3.5” dall&#39;interno del telaio all&#39;esterno, attraverso la finestra laterale della paratia.</p><p><img src="'+F+'" alt=""><br><br></p><p>7.2 Inserire il jack audio nella presa sul lato del Raspberry.</p><p><img src="'+O+'" alt=""><br><br></p><p>7.3 Far passare l&#39;altra estremità del cavo (connettore Mini USB) dalla parte anteriore del telaio.</p><p><img src="'+q+'" alt=""><br><br></p><p>7.4 Appoggiare il coperchio sulle paratie laterali, facendo in modo che le feritoie e gli incastri siano tutti correttamente impegnati nelle paratie.</p><p><img src="'+Z+'" alt=""></p><p><img src="'+$+'" alt=""><br><br></p><p>7.5 Inserire la vite M3 10mm nel foro posto nella linguetta presente nella parte inferiore del coperchio e avvirarla al dado autobloccante. <img src="'+w+'" alt=""><br><br></p><p>7.6 Montare le ruote facendole scorrere nei semiassi, facendo attenzione a non forzare il telaio. Si consiglia di fare una leggera forza agendo direttamente su motori e ruote. <img src="'+L+'" alt=""><br><br></p><p>7.7 Collegare il connettore Micro USB all&#39;altoparlante.</p><p><img src="'+J+'" alt=""></p><h2 id="_8-accensione" tabindex="-1">8 - Accensione <a class="header-anchor" href="#_8-accensione" aria-hidden="true">#</a></h2><p>8.1 Collegare la spina Micro USB nella presa presente sul Raspberry, accessibile dalla finestra sulla paratia laterale. <img src="'+G+'" alt=""></p>',85),Q=[K];function W(X,Y,ee,ae,ie,te){return i(),a("div",null,Q)}const le=e(H,[["render",W]]);export{oe as __pageData,le as default};
